import org.gradle.api.artifacts.dsl.DependencyHandler/** * 作者：hsicen  8/23/21 16:28 * 邮箱：codinghuang@163.com * 作用： * 描述：Dependence extension */fun DependencyHandler.kapt(list: List<String>) {    list.forEach { dependency ->        add("kapt", dependency)    }}fun DependencyHandler.implementation(list: List<String>) {    list.forEach { dependency ->        add("implementation", dependency)    }}fun DependencyHandler.androidTestImplementation(list: List<String>) {    list.forEach { dependency ->        add("androidTestImplementation", dependency)    }}fun DependencyHandler.testImplementation(list: List<String>) {    list.forEach { dependency ->        add("testImplementation", dependency)    }}fun DependencyHandler.debugImplementation(list: List<String>) {    list.forEach { dependency ->        add("debugImplementation", dependency)    }}