import org.gradle.api.artifacts.dsl.DependencyHandlerimport org.gradle.kotlin.dsl.project/** * 作者：hsicen  8/23/21 16:28 * 邮箱：codinghuang@163.com * 作用： * 描述：Dependence extension *//** * kapt denpendency library list. * @receiver DependencyHandler * @param list List<String> */fun DependencyHandler.kapt(list: List<String>) {  list.forEach { dependency ->    add("kapt", dependency)  }}/** * implementation dependency library list. * @receiver DependencyHandler * @param list List<String> */fun DependencyHandler.implementation(list: List<String>) {  list.forEach { dependency ->    add("implementation", dependency)  }}/** * implementation android test library list. * @receiver DependencyHandler * @param list List<String> */fun DependencyHandler.androidTestImplementation(list: List<String>) {  list.forEach { dependency ->    add("androidTestImplementation", dependency)  }}/** * implementation test library list. * @receiver DependencyHandler * @param list List<String> */fun DependencyHandler.testImplementation(list: List<String>) {  list.forEach { dependency ->    add("testImplementation", dependency)  }}/** * implementaion debug library list. * @receiver DependencyHandler * @param list List<String> */fun DependencyHandler.debugImplementation(list: List<String>) {  list.forEach { dependency ->    add("debugImplementation", dependency)  }}/** * implementation project * @receiver DependencyHandler * @param projectName String */fun DependencyHandler.implementationModule(projectName: String) {  add("implementation", project(projectName))}/** * implementation projects * @receiver DependencyHandler * @param modules List<String> */fun DependencyHandler.implementationModules(modules: List<String>) {  modules.forEach { dependency ->    add("implementation", project(dependency))  }}